INSERT INTO `mydb`.`ABILITES` (`ability_id`, `ABILITIY`, `Description`, `Cost`, `Cooldown`, `Ability_Range`, `ABILITIES_TYPE_abilities_type_id`, `CHAMPIONS_champion_id`) VALUES (59, 'Unbreakable', 'Braum raises his shield in a direction for several seconds, intercepting all projectiles causing them to hit him and be destroyed. He negates the damage of the first attack completely and reduces the damage of all subsequent attacks from this direction.Braum reduces incoming damage and blocks for allies behind him.Braum raises his shield in a direction for 3/3.25/3.5/3.75/4 seconds negating the damage of the next attack from this direction. Subsequent attacks deal 30/32.5/35/37.5/40% reduced damage.Braum intercepts projectiles, causing them to hit him and be destroyed.Braum gains 10% Movement Speed for the duration.', '30/35/40/45/50 Mana', '18 / 16 / 14 / 12 / 10', '25000', 4, 12);
INSERT INTO `mydb`.`ABILITES` (`ability_id`, `ABILITIY`, `Description`, `Cost`, `Cooldown`, `Ability_Range`, `ABILITIES_TYPE_abilities_type_id`, `CHAMPIONS_champion_id`) VALUES (60, 'Glacial Fissure', 'Braum slams the ground, knocking up enemies nearby and in a line in front of him. A fissure is left along the line that slows enemies.Braum slams the ground, knocking up enemies nearby and in a line in front of him. A fissure is left along the line that slows enemies.Enemies hit take 150/250/350 (+60% Ability Power) magic damage. The first champion hit is knocked up for 1/1.25/1.5 seconds, subsequent enemies are knocked up briefly.For the next 4 seconds enemies that enter the area are slowed by 40/50/60% for 0.25 seconds.', '100 Mana', '140 / 120 / 100', '1250', 5, 12);
