INSERT INTO `mydb`.`ABILITES` (`ability_id`, `ABILITIY`, `Description`, `Cost`, `Cooldown`, `Ability_Range`, `ABILITIES_TYPE_abilities_type_id`, `CHAMPIONS_champion_id`) VALUES (70, 'Petrifying Gaze', 'Cassiopeia releases a swirl of magical energy from her eyes, stunning any enemies in front of her that are facing her and slowing any others with their back turned.Cassiopeia deals 150/250/350 (+50% Ability Power) magic damage to all enemies in front of her. Enemies facing her are stunned for 2 seconds while enemies facing away are slowed by 60%.', '100 Mana', '120 / 110 / 100', '825', 5, 14);
