INSERT INTO `mydb`.`ABILITES` (`ability_id`, `ABILITIY`, `Description`, `Cost`, `Cooldown`, `Ability_Range`, `ABILITIES_TYPE_abilities_type_id`, `CHAMPIONS_champion_id`) VALUES (260, 'Ethereal Chains', 'LeBlanc flings illusionary chains towards a target location. If it hits an enemy unit, it will deal initial magic damage and slow their Movement Speed by 25%. If the target remains shackled for 2 seconds, the target takes additional magic damage and is unable to move.Launches a chain that shackles the first unit hit to LeBlanc, dealing 40/65/90/115/140 (+50% Ability Power) magic damage and slowing their Movement Speed by 25%.If the target remains shackled for 1.5 seconds they take an additional 40/65/90/115/140 (+50% Ability Power) magic damage and are unable to move for 1.5 seconds.', '80 Mana', '14 / 12.5 / 11 / 9.5 / 8', '925', 4, 50);
