INSERT INTO `mydb`.`ABILITES` (`ability_id`, `ABILITIY`, `Description`, `Cost`, `Cooldown`, `Ability_Range`, `ABILITIES_TYPE_abilities_type_id`, `CHAMPIONS_champion_id`) VALUES (301, 'Call of the Void', 'Malzahar opens up two portals to the Void. After a short delay, they fire projectiles that deal Magic Damage and silence enemy champions.Malzahar opens two portals to the Void. After a short delay, they fire projectiles that deal 80/135/190/245/300 (+80% Ability Power) Magic Damage and silence champions for 1.4/1.8/2.2/2.6/3 seconds.', '80/85/90/95/100 Mana', '9', '900', 2, 58);
