INSERT INTO `mydb`.`ABILITES` (`ability_id`, `ABILITIY`, `Description`, `Cost`, `Cooldown`, `Ability_Range`, `ABILITIES_TYPE_abilities_type_id`, `CHAMPIONS_champion_id`) VALUES (361, 'Ice Blast', 'Nunu launches a ball of ice at an enemy unit, dealing damage and temporarily slowing their Movement and Attack Speeds.Nunu launches a ball of ice at an enemy unit, dealing 85/130/175/225/275 (+100% Ability Power) magic damage and slowing their Movement Speed by 20/30/40/50/60% and Attack Speed by 25% for 3 seconds.', '75/85/95/105/115 Mana ', '6', '550', 4, 69);
