INSERT INTO `mydb`.`ABILITES` (`ability_id`, `ABILITIY`, `Description`, `Cost`, `Cooldown`, `Ability_Range`, `ABILITIES_TYPE_abilities_type_id`, `CHAMPIONS_champion_id`) VALUES (366, 'Reckless Swing', 'Olaf attacks with such force that it deals true damage to his target and himself, refunding the Health cost if he destroys the target.Olaf ferociously swings his axes, dealing 70/115/160/205/250 (+40% Attack Damage) true damage to his target. This ability\'s cost is equal to 30% of the total damage dealt, but the cost is refunded if it kills the target.Basic attacks lower the cooldown of Reckless Swing by 1 second.', '21/34.5/48/61.5/75 Health', '12 / 11 / 10 / 9 / 8', '325', 4, 70);
