INSERT INTO `mydb`.`ABILITES` (`ability_id`, `ABILITIY`, `Description`, `Cost`, `Cooldown`, `Ability_Range`, `ABILITIES_TYPE_abilities_type_id`, `CHAMPIONS_champion_id`) VALUES (371, 'Command: Protect', 'Orianna commands her ball to attach to an allied champion, shielding them and dealing magic damage to any enemies it passes through on the way. Additionally, the ball grants additional Armor and Magic Resist to the champion it is attached to.Passive: The ball adds 10/15/20/25/30 Armor and Magic Resist to the allied champion it is attached to.Active: Orianna commands her ball to travel to and attach onto an allied champion, shielding them for 4 seconds from the next 80/120/160/200/240 (+40% Ability Power) damage.Enemies the ball passes through along the way are damaged for 75% of the shield value: 60/90/120/150/180 (+30% Ability Power).', '60 Mana', '9', '1095', 4, 71);
INSERT INTO `mydb`.`ABILITES` (`ability_id`, `ABILITIY`, `Description`, `Cost`, `Cooldown`, `Ability_Range`, `ABILITIES_TYPE_abilities_type_id`, `CHAMPIONS_champion_id`) VALUES (372, 'Command: Shockwave', 'Orianna commands her ball to unleash a shockwave, dealing magic damage and launching nearby enemies towards the ball after a short delay.Orianna commands her ball to unleash a shockwave after a brief delay, dealing 150/225/300 (+70% Ability Power) magic damage to nearby enemies and flinging them into the air a set distance in the direction of the ball.', '100/125/150 Mana', '120 / 105 / 90', '815', 5, 71);
