INSERT INTO `mydb`.`ABILITES` (`ability_id`, `ABILITIY`, `Description`, `Cost`, `Cooldown`, `Ability_Range`, `ABILITIES_TYPE_abilities_type_id`, `CHAMPIONS_champion_id`) VALUES (437, 'Vorpal Blade', 'Damages and life taps a target unit, healing allies that attack the target.Deals 60/100/140/180/220 (+60% Ability Power) magic damage to target unit and marks them for 5 seconds.Allied spells and attacks against this target heal the attackers 6/10/14/18/22 (+ 1.5% of maximum Health) over 3 seconds.Killing an enemy with Vorpal Blade restores 2/3/5/6/7 (+0.5% of maximum Health) Health instantly instead.', '60 Energy', '6 / 5.5 / 5 / 4.5 / 4', '475', 2, 84);
INSERT INTO `mydb`.`ABILITES` (`ability_id`, `ABILITIY`, `Description`, `Cost`, `Cooldown`, `Ability_Range`, `ABILITIES_TYPE_abilities_type_id`, `CHAMPIONS_champion_id`) VALUES (438, 'Feint', 'Shen shields himself, absorbing incoming damage for a few seconds.Shen shields himself, absorbing the next 60/100/140/180/220 (+60% Ability Power) damage. Lasts up to 3 seconds.While Feint holds, his attacks reduce Ki Strike\'s cooldown by an additional 1 second.', '50 Energy', '9 / 8 / 7 / 6 / 5', 'N/A', 3, 84);
INSERT INTO `mydb`.`ABILITES` (`ability_id`, `ABILITIY`, `Description`, `Cost`, `Cooldown`, `Ability_Range`, `ABILITIES_TYPE_abilities_type_id`, `CHAMPIONS_champion_id`) VALUES (439, 'Shadow Dash', 'Shen dashes rapidly toward a target location, taunting enemy champions he encounters and dealing minor damage.Shen dashes in a direction, dealing 50/85/120/155/190 (+50% Ability Power) magic damage to champions and taunting champions he collides with for 1.5 second(s).Taunted enemies are forced to attack Shen and their basic attacks deal 50% reduced damage.Restores 40 Energy per champion affected.', '100 Energy', '16 / 14 / 12 / 10 / 8', '600', 4, 84);
INSERT INTO `mydb`.`ABILITES` (`ability_id`, `ABILITIY`, `Description`, `Cost`, `Cooldown`, `Ability_Range`, `ABILITIES_TYPE_abilities_type_id`, `CHAMPIONS_champion_id`) VALUES (440, 'Stand United', 'Shen shields target allied champion from incoming damage, and soon after teleports to their location.Places a shield on target allied champion, absorbing 250/550/850 (+135% Ability Power) damage for 5 seconds. After channeling for 3 seconds, Shen teleports to the target\'s location.', 'N/A', '180 / 160 / 120', '25000', 5, 84);
